<?php/*** Reset user password* This file will allow a user to reset *  their password to a randomly generated password.* This new password will be set in the database* and it will be emailed to the user.* @author Nick Korbel <lqqkout13@users.sourceforge.net>* @version 09-11-04* @package phpScheduleIt** Copyright (C) 2003 - 2004 phpScheduleIt* License: GPL, see LICENSE*//*** Template class*/include_once('lib/Template.class.php');include_once('lib/Auth.class.php');/*** PHPMailer*/include_once('lib/PHPMailer.class.php');$t = new Template(translate('Forgot Password'));// Print HTML header$t->printHTMLHeader();// Start main table$t->startMain();// Set status to false so we print the form by default$status = false;// Determine if we are changing the passwordif ( (isset($_POST['email_address'])) && strstr($_SERVER['HTTP_REFERER'],$_SERVER['PHP_SELF']))    $status = changePassword('');// Print form or success messageif ($status)    printSuccess();else    printPasswordForm();// End main table$t->endMain();// Print HTML footer$t->printHTMLFooter();/*** Print password form* This function prints out a form allowing*  a user to enter their email to change*  their forgotten password* @param none*/function printPasswordForm() {?><h5 align="center"><?=translate('This will change your password to a new, randomly generated one.')?></h5><h5 align="center"><?=translate('your new password will be set')?></h5><br /><div align="center" style="border: solid 1px #CCCCCC"><form name="new_pwd" method="post" action="<?=$_SERVER['PHP_SELF']?>">  <?=translate('Email')?> <input type="text" class="textbox" name="email_address" />  <br />  <input type="submit" class="button" name="changePassword" value="<?=translate('Change Password')?>" />  <input type="button" class="button" name="cancel" value="<?=translate('Cancel')?>" onclick="javascript: document.location='index.php';" /></form></div><?}/*** Print success message after changed password* This function simply prints out a message informing*  the user that thier password was changed and how to*  log in now* @param none*/function printSuccess() {	CmnFns::do_message_box(translate('Your new passsword has been emailed to you.'), 'width: 75%;');}?>